package constructr;

public class Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("This is beginning");
		
		for (int i=0; i<=100;i++) {
			
			if(i%2 == 0) {
				System.out.println("This is even number:"+i);
			}
			else {
				System.out.println("this is odd number:"+i);
			}
		}

	}

}
