package imp_java_programms;

public class Even_odd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int num = 20;
         
         for(int i=1;i<=num;i++) {
         if(i%2 == 0) {
        	 System.out.println(i+"this is even number");
         }
        	 else
        		 System.out.println(i+"this is odd number");
         }
	}
}


