
public class swap_with_using {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1,b=3,c=a;
		System.out.println("value of a: "+a+" value of b:"+b+" value of c:"+c);
		a=b;b=c;
		System.out.println("value of a: "+a+" value of b:"+b+" value of c:"+c);
		

	}

}
